import React from "react";
import Highcharts from "highcharts/highstock";
import HighchartsReact from "highcharts-react-official";

const transformChartData = (options, array) => {
  const dataLength = array.length;

  for (var i = 0; i < dataLength; i += 1) {
    options.series[0].data.push([
      array[i][0], // the date
      array[i][1], // open
      array[i][2], // high
      array[i][3], // low
      array[i][4], // close
    ]);

    options.series[1].data.push([
      array[i][0], // the date
      array[i][5], // the volume
    ]);
  }
  return options;
};

const StockGraph = (props) => {
  if (!props.data.open) {
    return <div>Enter Something</div>;
  }

  // const options = {
  //   title: {
  //     text: "My stock chart",
  //   },
  //   series: [
  //     {
  //       data: props.data.open,
  //     },
  //   ],
  // };
  const mockData = props.data;
  console.log(props.data);
  let groupingUnits = [
    [
      "week", // unit name
      [1], // allowed multiples
    ],
    ["month", [1, 2, 3, 4, 6]],
  ];

  let mockOptions = {
    rangeSelector: {
      selected: 1,
    },

    title: {
      text: "AAPL Historical",
    },

    yAxis: [
      {
        labels: {
          align: "right",
          x: -3,
        },
        title: {
          text: "OHLC",
        },
        height: "60%",
        lineWidth: 2,
        resize: {
          enabled: true,
        },
      },
      {
        labels: {
          align: "right",
          x: -3,
        },
        title: {
          text: "Volume",
        },
        top: "65%",
        height: "35%",
        offset: 0,
        lineWidth: 2,
      },
    ],

    tooltip: {
      split: true,
    },

    series: [
      {
        type: "candlestick",
        name: "AAPL",
        data: [],
        dataGrouping: {
          units: groupingUnits,
        },
      },
      {
        type: "column",
        name: "Volume",
        data: [],
        yAxis: 1,
        dataGrouping: {
          units: groupingUnits,
        },
      },
    ],
  };
  const newOptions = transformChartData(mockOptions, mockData);
  console.log(newOptions);
  console.log(props.data);
  return (
    <div>
      <div>
        <HighchartsReact
          highcharts={Highcharts}
          constructorType={"stockChart"}
          options={newOptions}
        />
      </div>
    </div>
  );
};

export default StockGraph;
