import React from "react";
import { fetchData } from "../actions";
import { connect } from "react-redux";
import StockGraph from "./StockGraph";

class SearchBar extends React.Component {
  state = { term: "" };

  onFormSubmit = async (event) => {
    event.preventDefault();
    await this.props.fetchData(this.state.term);

    this.props.onTermSubmit(this.props.data);
    // console.log(this.state.term);

    // this.props.onTermSubmit(this.state.term);
    //Make sure to call callback from parent
  };

  onInputChange = (event) => {
    this.setState({ term: event.target.value });
  };

  render() {
    return (
      <div className="search-bar ui segment">
        <form className="ui form" onSubmit={this.onFormSubmit}>
          <div className="field">
            <label>Index Search</label>
            <input
              type="text"
              value={this.state.term}
              onChange={this.onInputChange}
            ></input>
          </div>
        </form>
        {/* <div>
          <StockGraph data={this.props.data} />
        </div> */}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  // console.log(state.stock.data);
  return { data: state.stock.data };
};

export default connect(mapStateToProps, { fetchData })(SearchBar);
