import React from "react";
import SearchBar from "./SearchBar";
// import _ from "lodash";
import StockGraph from "./StockGraph";

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      stock: {
        name: null,
        dataStore: [],
      },

      nifty: {
        name: null,
        dataStore: [],
      },
    };
  }

  onTermSubmit = (data) => {
    var DataStore = [];

    var temp = data["Time Series (Daily)"];
    for (var tempData in temp) {
      DataStore.push({
        Date: tempData,
        open: temp[tempData]["1. open"],
        high: temp[tempData]["2. high"],
        low: temp[tempData]["3. low"],
        close: temp[tempData]["4. close"],
        volume: temp[tempData]["5. volume"],
      });
    }
    this.setState((stock = { dataStore: DataStore, name: data["Meta Data"] }));
  };
  onTermSubmit_nifty = (data) => {
    var DataStore = [];

    var temp = data["Time Series (Daily)"];
    for (var tempData in temp) {
      DataStore.push({
        Date: tempData,
        open: temp[tempData]["1. open"],
        high: temp[tempData]["2. high"],
        low: temp[tempData]["3. low"],
        close: temp[tempData]["4. close"],
        volume: temp[tempData]["5. volume"],
      });
    }
    this.setState((nifty = { dataStore: DataStore, name: data["Meta Data"] }));
  };

  render() {
    return (
      <div>
        <div className=" ui container ">
          <SearchBar onTermSubmit={this.onTermSubmit} />
        </div>
        <div className=" ui container ">
          <StockGraph data={this.state} />
          {/* <NiftyGraph /> */}
        </div>
      </div>
    );
  }
}

export default App;
