import React from "react";
import HighStock from "highcharts/highstock";
import HighchartsReact from "highcharts-react-official";

const StockGraph = (props) => {
  if (props.data.length === 0) {
    return <div>Enter Something</div>;
  }
  console.log(props.data);

  let options = {
    rangeSelector: {
      selected: 1,
    },

    title: {
      text: "Price Chart",
    },
    xAxis: {
      type: "datetime",
      dateTimeLabelFormats: {
        // don't display the dummy year
        day: "%Y-%b-%d",
      },
      title: {
        text: "Date",
      },
    },

    yAxis: [
      {
        labels: {
          align: "right",
          x: -3,
        },
        title: {
          text: "OHLC",
        },
        height: "60%",
        lineWidth: 2,
        resize: {
          enabled: true,
        },
      },
      {
        labels: {
          align: "right",
          x: -3,
        },
        title: {
          text: "Volume",
        },
        top: "65%",
        height: "35%",
        offset: 0,
        lineWidth: 2,
      },
    ],

    tooltip: {
      split: true,
    },

    series: [
      {
        type: "candlestick",
        name: "AAPL",
        data: [],
      },
      {
        type: "column",
        name: "Volume",
        data: [],
        yAxis: 1,
      },
    ],
  };
  console.log(props.data[0]["Date"]);

  props.data.forEach(function (point) {
    point[0] = new Date(point[0]).getTime();
  });
  console.log(props.data[0]["Date"]);
  const dataLength = props.data.length;
  for (var i = dataLength - 1; i >= 0; i -= 1) {
    options.series[0].data.push([
      new Date(props.data[i]["Date"]).getDate(), // the date
      parseInt(props.data[i]["open"]), // open
      parseInt(props.data[i]["high"]), // high
      parseInt(props.data[i]["low"]), // low
      parseInt(props.data[i]["close"]), // close
    ]);

    options.series[1].data.push([
      props.data[i]["Date"], // the date
      parseInt(props.data[i]["volume"]), // the volume
    ]);
  }
  console.log(options);
  return (
    <div className="App">
      <HighchartsReact
        highcharts={HighStock}
        constructorType={"stockChart"}
        options={options}
      />
    </div>
  );
};

export default StockGraph;
